/* eslint-disable */
/*global google*/
<template>
  <v-app>
    <template><div>
    <v-app-bar>
       <v-btn link icon to="/">
          <v-icon>mdi-qrcode-scan</v-icon>
       </v-btn>
       <img src="logo.png" height="13"/>

         <v-layout v-if="$firebase.auth().currentUser" justify-end style="padding-top:6px">
              <v-chip link to="/rating"  text-color="orange" color="transparent">
                <v-icon color="orange" small>mdi-star-face</v-icon>
                {{$store.state.user.bonus}}
              </v-chip>

              <v-chip link to="/wallet" text-color="#07C01A" color="transparent">
                <v-icon color="#07C01A" small>mdi-coin-outline</v-icon>
                  {{$store.state.user.tokens}}
              </v-chip>
              <v-avatar right @click="$router.push('/profile')" size="32px">
                <v-img v-bind:src="$store.state.user.avatar"></v-img>
              </v-avatar>
           </v-layout>
         </v-app-bar> 
    </div>
    </template>
    <v-content style="margin-bottom:60px">
      <router-view class="view"></router-view>
    </v-content>

    <v-bottom-navigation v-if="$firebase.auth().currentUser" grow fixed color="orange">
        <v-btn value="qr" link icon to="/qr">
           <v-icon>mdi-qrcode-scan</v-icon>
        </v-btn>

        <v-btn value="rating" link icon to="/rating">
            <v-icon>mdi-podium-gold</v-icon>
        </v-btn>

        <v-btn value="wallet" link icon to="/wallet">
            <v-icon>mdi-coin-outline</v-icon>
        </v-btn>

        <v-btn value="find" link icon to="/find">
            <v-icon>mdi-map-marker-radius</v-icon>
        </v-btn>

        <v-btn value="ads" link icon to="/ads">
            <v-icon>mdi-plus-box-outline</v-icon>
        </v-btn>
    </v-bottom-navigation>
  </v-app>
</template>

<script>
  //import Wallet from './components/wallet.vue';
  //import Profile from './components/profile.vue';
  export default {
    name: 'App',
    data: () => ({
     publicPath: process.env.BASE_URL,
     bottomNav: false,
     appUser:false
    }),   
    /*async created() {
      let ref = this;
      if(ref.$firebase.auth().currentUser){
        await ref.$store.state.db.collection('users').doc(ref.$firebase.auth().currentUser.uid).get().then(
          doc=>{
            ref.appUser = doc.data();
          }
        );
      }
    },*/
    methods: {
      signOut(){
        let ref = this;
        ref.$firebase.auth().signOut().then(
          ()=>{
            ref.$store.state.user = false;
            ref.appUser = false;
            ref.$router.push({path:'signup', name:'Signup', params:{logout:1}});
          }
        );
      }
    }
  };
</script>
